package codeBreakerzz;

public class primeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	//Prime number
	for(int n = 2;n<=1000; n++)
	{
		for(int j = 2; j<=n; j++) 
		{	
			
		if(j==n) 
		{
			System.out.println(n);
		}
		if(n%j==0)
		{
		break;
	}
		}
	}
	}
}
	
	
		
	
	

		
	
		
	
	

		
	
	


